//=============================== FUNCTIONS ===================================

const ImageAnimation* get_confirm_icon_animation()
{
	return &confirm_icon;
}

const uint32_t get_image_animation_duration(const ImageAnimation* img_animation)
{
	uint32_t duration = 0;

	for(int i = 0; i < img_animation->length; i++){
		duration += img_animation->frames[i].duration;
	}

	return duration;
}

const Image* get_image_animation_frame(const ImageAnimation* img_animation, const uint32_t elapsed)
{
	uint32_t adjusted_elapsed = elapsed % get_image_animation_duration(img_animation);
	uint32_t current_time = 0;

	for(int i = 0; i < img_animation->length; i++){
		current_time += img_animation->frames[i].duration;

		if(adjusted_elapsed <= current_time)
			return img_animation->frames[i].image;
	}
}
